// src/PostDetail.js
import React from 'react';
import './PostDetail.css';

function PostDetail({ post }) {
  if (!post) {
    return <div>게시물을 찾을 수 없습니다.</div>;
  }

  return (
    <div className="PostDetail">
      <h2>{post.title}</h2>
      <p>{post.content}</p>
      <button onClick={goBack}>뒤로 가기</button>
    </div>
  );
}

export default PostDetail;
