// ... 기존 imports 유지 ...

function App() {
  const [posts, setPosts] = useState(initialPosts);
  // selectedPostId는 더 이상 필요하지 않으므로 제거

  const addPost = ({ title, content }) => {
    const newPost = {
      id: posts.length + 1,
      title,
      content,
    };
    setPosts([...posts, newPost]);
  };

  return (
    <div className="App">
      <h1>게시판</h1>
      <nav>
        <Link to="/">목록</Link>
        <Link to="/new">새 게시물 작성</Link>
      </nav>
      <Routes>
        <Route path="/" element={<PostList posts={posts} />} />
        <Route 
          path="/post/:id" 
          element={
            <PostDetail 
              post={posts.find(p => p.id === parseInt(useParams().id))} 
            />
          } 
        />
        <Route path="/new" element={<NewPost addPost={addPost} />} />
      </Routes>
    </div>
  );
}
