import React, { useState } from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import './App.css';
import { posts as initialPosts } from './data';
import PostList from './PostList';
import PostDetail from './PostDetail';
import NewPost from './NewPost';

function App() {
  const [posts, setPosts] = useState(initialPosts);
  const [currentView, setCurrentView] = useState('list');
  const [selectedPostId, setSelectedPostId] = useState(null);

  const selectPost = (id) => {
    setSelectedPostId(id);
  };

  const addPost = ({ title, content }) => {
    const newPost = {
      id: posts.length + 1,
      title,
      content,
    };
    setPosts([...posts, newPost]);
  };

  return (
    <div className="App">
      <h1>게시판</h1>
      <nav>
        <Link to="/">목록</Link>
        <Link to="/new">새 게시물 작성</Link>
      </nav>
      <Routes>
        <Route path="/" element={<PostList posts={posts} selectPost={selectPost} />} />
        <Route path="/post/:id" element={<PostDetail post={posts.find(p => p.id === selectedPostId)} />} />
        <Route path="/new" element={<NewPost addPost={addPost} />} />
      </Routes>
    </div>
  );
}

export default App;
