// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

import './App.css';
import { posts as initialPosts } from './data';
import PostList from './PostList';
import PostDetail from './PostDetail';
import NewPost from './NewPost';

function App() {
  const [posts, setPosts] = useState(initialPosts);
  const [currentView, setCurrentView] = useState('list'); // 현재 화면 상태를 나타냄 
  const [selectedPostId, setSelectedPostId] = useState(null);

  const selectPost = (id) => {
    setSelectedPostId(id);
    setCurrentView('detail');
  };

  const addPost = ({ title, content }) => {
    const newPost = {
      id: posts.length + 1,
      title,
      content,
    };
    setPosts([...posts, newPost]);
  };

  const goBack = () => {
    setCurrentView('list');
  };

  const renderView = () => {
    if (currentView === 'list') {
      return <PostList posts={posts} selectPost={selectPost} />;
    } else if (currentView === 'detail') {
      const post = posts.find((p) => p.id === selectedPostId);
      return <PostDetail post={post} goBack={goBack} />;
    } else if (currentView === 'new') {
      return <NewPost addPost={addPost} goBack={goBack} />;
    }
  };

  return (
    <div className="App">
      <h1>게시판</h1>
      {currentView === 'list' && (
        <button onClick={() => setCurrentView('new')}>새 게시물 작성</button>
      )}
      {renderView()}
    </div>
  );
}

export default App;
