// src/PostList.js
import React from 'react';
import './PostList.css'; // 별도의 CSS 파일을 생성하여 임포트합니다.

function PostList({ posts, selectPost }) {
  return (
    <div className="PostList">
      <h2>게시물 목록</h2>
      <ul>
        {posts.map((post) => (
          <li key={post.id}>
            <a href="#" onClick={() => selectPost(post.id)}>
              {post.title}
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default PostList;
